import React from 'react';
import { useNavigate } from 'react-router-dom';
import iconeIcon from './icone.png';
import heartIcon from './heart.png';
import panierIcon from './panier.png';
import boxIcon from './box.png';

export default function Navbar({ user, cartCount, onLogout }) {
  const navigate = useNavigate();

  return (
    <nav className="navbar navbar-expand-lg navbar-light bg-white shadow-sm sticky-top">
      <style>{`
        .btn-3d {
          cursor: pointer;
          border: 0;
          background: transparent;
          color: #fff;
          padding: 6px 10px;
          font-weight: bold;
          font-size: 12px;
          text-transform: uppercase;
          letter-spacing: 0.1em;
          position: relative;
        }

        .block-cube {
          position: relative;
        }

        .block-cube .bg-top {
          position: absolute;
          height: 6px;
          background: linear-gradient(90deg, #dc3545 0%, #b02a37 100%);
          bottom: 100%;
          left: 2px;
          right: -2px;
          transform: skew(-45deg, 0);
          transition: all 0.2s ease-in-out;
        }

        .block-cube .bg-right {
          position: absolute;
          background: #dc3545;
          top: -2px;
          bottom: 2px;
          width: 6px;
          left: 100%;
          transform: skew(0, -45deg);
          transition: all 0.2s ease-in-out;
        }

        .block-cube .bg {
          position: absolute;
          left: 0;
          top: 0;
          right: 0;
          bottom: 0;
          background: linear-gradient(90deg, #dc3545 0%, #b02a37 100%);
          transition: all 0.2s ease-in-out;
        }

        .block-cube .bg-inner {
          background: #a02622;
          position: absolute;
          left: 2px;
          top: 2px;
          right: 2px;
          bottom: 2px;
          transition: all 0.2s ease-in-out;
        }

        .block-cube-hover:hover .bg-inner {
          top: 100% !important;
        }

        .text-label {
          position: relative;
          z-index: 3;
        }
      `}</style>
      <div className="container-fluid">
        {/* Brand */}
        <button
          className="navbar-brand btn btn-link text-decoration-none"
          onClick={() => navigate('/')}
          style={{ cursor: 'pointer' }}
        >
          <span className="h5 fw-bold text-primary mb-0"><img 
                      src={iconeIcon}
                      alt="Icon"
                      style={{ width: '24px', height: '24px', marginRight: '8px' }}
                    /><h6 style={{ textAlign: 'center', marginBottom: '20px', color: 'var(--blue-dark)' }}>VOTRE BOUTIQUE</h6></span>
        </button>

        {/* Toggle Button */}
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarNav"
          aria-controls="navbarNav"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>

        {/* Nav Items */}
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav ms-auto">
            <li className="nav-item">
              <button
                onClick={() => navigate('/')}
                className="nav-link btn btn-link text-decoration-none"
              >
                Accueil
              </button>
            </li>
            <li className="nav-item">
              <button
                onClick={() => navigate('/wishlist')}
                className="nav-link btn btn-link text-decoration-none"
              >
                <img src={heartIcon} alt="Wishlist" style={{ width: '20px', height: '20px', marginRight: '8px' }} /> Liste de souhaits
              </button>
            </li>
            <li className="nav-item">
              <button
                onClick={() => navigate('/cart')}
                className="nav-link btn btn-link text-decoration-none position-relative"
              >
                <img src={panierIcon} alt="Cart" style={{ width: '20px', height: '20px', marginRight: '8px' }} /> Panier
                {cartCount > 0 && (
                  <span className="position-absolute top-0 start-100 translate-middle badge rounded-pill bg-danger">
                    {cartCount}
                  </span>
                )}
              </button>
            </li>
            <li className="nav-item">
              <button
                onClick={() => navigate('/purchases')}
                className="nav-link btn btn-link text-decoration-none"
              >
                <img src={boxIcon} alt="Purchases" style={{ width: '20px', height: '20px', marginRight: '8px' }} /> Mes achats
              </button>
            </li>
            <li className="nav-item ms-3">
              <span className="text-dark">
                Bonjour, <span className="text-primary fw-bold">{user.firstName}</span>
              </span>
            </li>
            <li className="nav-item ms-2">
              <button
                onClick={onLogout}
                className="btn-3d block-cube block-cube-hover"
              >
                <div className="bg-top"><div className="bg-inner"></div></div>
                <div className="bg-right"><div className="bg-inner"></div></div>
                <div className="bg"><div className="bg-inner"></div></div>
                <span className="text-label">DÃ©connexion</span>
              </button>
            </li>
          </ul>
        </div>
      </div>
    </nav>
  );
}
