import React, { useState, useEffect } from 'react';

export default function Purchases({ user }) {
  const [purchases, setPurchases] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchPurchases();
  }, []);

  async function fetchPurchases() {
    try {
      const res = await fetch(`http://localhost:4000/api/purchase/${user._id}`);
      const data = await res.json();
      setPurchases(data || []);
    } catch (err) {
      console.error('Erreur:', err);
    }
    setLoading(false);
  }

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 pt-20 flex items-center justify-center">
        <div className="text-xl text-gray-600">Chargement...</div>
      </div>
    );
  }

  if (purchases.length === 0) {
    return (
      <div className="min-h-screen bg-gray-50 pt-20">
        <div className="max-w-4xl mx-auto">
          <div className="bg-white rounded-lg shadow-lg p-8 text-center">
            <h2 className="text-3xl font-bold text-gray-800 mb-4">ðŸ“¦ Aucun achat</h2>
            <p className="text-gray-600">Vous n\'avez pas encore achetÃ© d\'articles</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 pt-20 pb-20">
      <div className="max-w-4xl mx-auto px-4">
        <h2 className="text-3xl font-bold text-gray-800 mb-8">ðŸ“¦ Historique des Achats</h2>

        <div className="space-y-4">
          {purchases.map((purchase) => (
            <div key={purchase._id} className="bg-white rounded-lg shadow-lg p-6">
              <div className="flex justify-between items-start">
                <div>
                  <h3 className="text-xl font-bold text-gray-800 mb-2">
                    {purchase.product_id?.name || 'Produit supprimÃ©'}
                  </h3>
                  <p className="text-gray-600 mb-2">
                    <span className="font-semibold">CatÃ©gorie:</span> {purchase.product_id?.category}
                  </p>
                  <p className="text-blue-600 font-bold text-lg">
                    ${purchase.product_id?.price || 'N/A'}
                  </p>
                </div>
                <div className="text-right">
                  <p className="text-sm text-gray-600">
                    {new Date(purchase.timestamp).toLocaleDateString()}
                  </p>
                  <span className="inline-block mt-2 px-3 py-1 bg-green-200 text-green-800 rounded-full text-sm">
                    âœ… AchetÃ©
                  </span>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
