import React from 'react';

export default function Wishlist({ wishlistItems, onRemove }) {
  if (wishlistItems.length === 0) {
    return (
      <div style={{ minHeight: '100vh' }} className="bg-light pt-5">
        <div className="container">
          <div className="card shadow-lg p-5 text-center">
            <h2 className="h3 fw-bold text-dark mb-3">❤️ Votre liste de souhaits est vide</h2>
            <p className="text-muted">Ajoutez des articles à votre liste!</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div style={{ minHeight: '100vh' }} className="bg-light pt-5 pb-5">
      <div className="container">
        <h2 className="h3 fw-bold text-dark mb-4">❤️ Ma Liste de Souhaits</h2>

        <style>{`
          :root {
            --blue-dark: #00326f;
            --blue-light: #0151aa;
            --bg-input: #0753b0ff;
            --gradient: linear-gradient(90deg, #00326f 0%, #041830ff 100%);
            --gradient-success: linear-gradient(90deg, #28a745 0%, #20c997 100%);
            --gradient-danger: linear-gradient(90deg, #dc3545 0%, #fd7e14 100%);
          }

          .btn-3d {
            width: 100%;
            cursor: pointer;
            border: 0;
            background: transparent;
            color: #fff;
            padding: 8px 12px;
            font-weight: bold;
            font-size: 12px;
            text-transform: uppercase;
            letter-spacing: 0.1em;
            position: relative;
          }

          .block-cube {
            position: relative;
          }

          .block-cube .bg-top {
            position: absolute;
            height: 10px;
            background: var(--gradient);
            bottom: 100%;
            left: 5px;
            right: -5px;
            transform: skew(-45deg, 0);
            transition: all 0.2s ease-in-out;
          }

          .block-cube .bg-right {
            position: absolute;
            background: var(--blue-dark);
            top: -5px;
            bottom: 5px;
            width: 10px;
            left: 100%;
            transform: skew(0, -45deg);
            transition: all 0.2s ease-in-out;
          }

          .block-cube .bg {
            position: absolute;
            left: 0;
            top: 0;
            right: 0;
            bottom: 0;
            background: var(--gradient);
            transition: all 0.2s ease-in-out;
          }

          .block-cube .bg-inner {
            background: var(--bg-input);
            position: absolute;
            left: 2px;
            top: 2px;
            right: 2px;
            bottom: 2px;
            transition: all 0.2s ease-in-out;
          }

          .block-cube-hover:hover .bg-inner {
            top: 100% !important;
          }

          .text-label {
            position: relative;
            z-index: 3;
          }

          .btn-danger-3d .bg-top,
          .btn-danger-3d .bg {
            background: var(--gradient-danger);
          }

          .btn-danger-3d .bg-inner {
            background: #dc3545;
          }
        `}</style>

        <div className="row">
          {wishlistItems.map((item) => (
            <div key={item._id} className="col-md-4 col-lg-3 mb-4">
              <div className="card shadow-lg h-100">
                <div className="card-body">
                  <h5 className="card-title">{item.name}</h5>
                  <p className="card-text text-muted small">{item.category}</p>
                  <div className="d-flex justify-content-between align-items-center mb-3">
                    <span className="text-primary fw-bold">${item.price}</span>
                    <span className="text-warning">⭐ {item.rating || 'N/A'}</span>
                  </div>
                  <button
                    onClick={() => onRemove(item._id)}
                    className="btn btn-danger btn-sm w-100"
                  >
                    ❌ Supprimer
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
