// Login component: handles user authentication form and calls backend API.
import React, { useState } from 'react';

export default function Login({ onLogin }) {
  const [email, setEmail] = useState('aya@test.com');
  const [password, setPassword] = useState('123456');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  async function login() {
    setLoading(true);
    try {
      const res = await fetch('http://localhost:4000/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password })
      });
      
      const data = await res.json();
      
      if (!res.ok) throw new Error(data.error);
      
      // On passe l'objet user complet (qui vient de la r√©ponse JSON du backend)
      onLogin(data.user); 
    } catch (err) {
      setError(err.message || 'Email ou mot de passe incorrect');
    }
    setLoading(false);
  }
  return (
    <div style={{ minHeight: '100vh' }} className="d-flex align-items-center justify-content-center bg-light">
      <div className="card shadow-lg p-4" style={{ width: 420 }}>
        <div className="text-center mb-4">
          <h1 className="fs-2 fw-bold text-primary mb-2">üõçÔ∏è RECO</h1>
          <p className="text-muted">Recommandations Intelligentes</p>
        </div>

        <h2 className="h4 text-center text-dark mb-4">Se Connecter</h2>

        <div className="mb-3">
          <input
            type="email"
            className="form-control form-control-lg"
            placeholder="Email"
            value={email}
            onChange={e => setEmail(e.target.value)}
          />
        </div>

        <div className="mb-3">
          <input
            type="password"
            className="form-control form-control-lg"
            placeholder="Mot de passe"
            value={password}
            onChange={e => setPassword(e.target.value)}
          />
        </div>

        <button
          onClick={login}
          disabled={loading}
          className="btn btn-primary btn-lg w-100 fw-bold"
        >
          {loading ? 'Connexion...' : 'üîê Se connecter'}
        </button>

        {error && (
          <div className="alert alert-danger mt-3 mb-0">‚ùå {error}</div>
        )}

        <div className="mt-4 p-3 bg-light rounded border">
          <p className="fw-bold small mb-2">üß™ Identifiants de test:</p>
          <p className="small mb-1">Email: <code>aya@test.com</code></p>
          <p className="small mb-0">Mot de passe: <code>123456</code></p>
        </div>
      </div>
    </div>
  );
}
