import React, { useState } from 'react';

export default function Cart({ cartItems, onRemove, onCheckout }) {
  const [loading, setLoading] = useState(false);

  const total = cartItems.reduce((sum, item) => sum + (item.price || 0), 0);

  async function handleCheckout() {
    setLoading(true);
    try {
      await onCheckout();
      alert('‚úÖ Achat r√©ussi!');
    } catch (err) {
      alert('‚ùå Erreur lors de l\'achat');
    }
    setLoading(false);
  }

  if (cartItems.length === 0) {
    return (
      <div className="min-h-screen bg-gray-50 pt-20">
        <div className="max-w-2xl mx-auto">
          <div className="bg-white rounded-lg shadow-lg p-8 text-center">
            <h2 className="text-3xl font-bold text-gray-800 mb-4">üõí Votre panier est vide</h2>
            <p className="text-gray-600">Commencez √† faire du shopping!</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 pt-20 pb-20">
      <div className="max-w-4xl mx-auto px-4">
        <h2 className="text-3xl font-bold text-gray-800 mb-6">üõí Votre Panier</h2>

        {/* Cart Items */}
        <div className="bg-white rounded-lg shadow-lg overflow-hidden mb-6">
          {cartItems.map((item) => (
            <div key={item._id} className="flex items-center justify-between p-6 border-b last:border-b-0">
              <div className="flex-1">
                <h3 className="text-xl font-bold text-gray-800">{item.name}</h3>
                <p className="text-gray-600">{item.category}</p>
                <p className="text-blue-600 font-bold text-lg mt-2">${item.price}</p>
              </div>
              <button
                onClick={() => onRemove(item._id)}
                className="px-4 py-2 bg-red-500 text-white rounded-lg hover:bg-red-600 transition"
              >
                Supprimer
              </button>
            </div>
          ))}
        </div>

        {/* Summary */}
        <div className="bg-white rounded-lg shadow-lg p-6">
          <div className="flex justify-between items-center mb-6">
            <span className="text-xl font-bold">Total:</span>
            <span className="text-3xl font-bold text-blue-600">${total.toFixed(2)}</span>
          </div>

          <button
            onClick={handleCheckout}
            disabled={loading}
            className="w-full px-6 py-3 bg-green-600 text-white font-bold rounded-lg hover:bg-green-700 transition disabled:bg-gray-400"
          >
            {loading ? 'Traitement...' : '‚úÖ Proc√©der au paiement'}
          </button>
        </div>
      </div>
    </div>
  );
}
