import React, { useEffect, useState } from 'react';

export default function Recommendations({ user }) {
  // On utilise 'recs' pour stocker les produits
  const [recs, setRecs] = useState([]);
  const [loading, setLoading] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState('All');

  useEffect(() => {
    const fetchRecs = async () => {
      if (!user || !user._id) return;
      
      setLoading(true);
      try {
        // Remplacement de axios par fetch pour Ã©viter l'erreur "not defined"
        const response = await fetch(`http://localhost:4000/api/recommend/${user._id}`);
        const data = await response.json();
        
        // ATTENTION : Utilisez setRecs (le nom de votre state)
        setRecs(data); 
      } catch (error) {
        console.error("Erreur lors du chargement des recommandations:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchRecs();
  }, [user._id]); // Recharge si l'utilisateur change

  async function buy(productId) {
    try {
      const response = await fetch('http://localhost:4000/api/purchase', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ userId: user._id, productId })
      });
      if (response.ok) {
        alert('Produit achetÃ© ! Vos recommandations vont bientÃ´t se mettre Ã  jour.');
      }
    } catch (error) {
      alert("Erreur lors de l'achat");
    }
  }

  // Get unique categories
  const categories = ['All', ...new Set(recs.map(p => p.category))];

  // Filter recommendations
  const filteredRecs = selectedCategory === 'All' ? recs : recs.filter(p => p.category === selectedCategory);

  if (loading) return <div className="container mt-4">chargement des recommandations...</div>;

  return (
    <div className="container mt-4">
      <h3 className="mb-4">ðŸŽ¯ Recommandations pour {user.firstName}</h3>
      
      <div className="mb-4">
        <label htmlFor="category-filter" className="form-label fw-bold">Filtrer par catÃ©gorie:</label>
        <select
          id="category-filter"
          className="form-select"
          value={selectedCategory}
          onChange={(e) => setSelectedCategory(e.target.value)}
        >
          {categories.map(cat => (
            <option key={cat} value={cat}>{cat}</option>
          ))}
        </select>
      </div>

      <style>{`
        :root {
          --blue-dark: #00326f;
          --blue-light: #0151aa;
          --bg-input: #0753b0ff;
          --gradient: linear-gradient(90deg, #00326f 0%, #041830ff 100%);
          --gradient-success: linear-gradient(90deg, #28a745 0%, #20c997 100%);
        }

        .btn-3d {
          width: 100%;
          cursor: pointer;
          border: 0;
          background: transparent;
          color: #fff;
          padding: 10px 14px;
          font-weight: bold;
          font-size: 14px;
          text-transform: uppercase;
          letter-spacing: 0.1em;
          position: relative;
        }

        .block-cube {
          position: relative;
        }

        .block-cube .bg-top {
          position: absolute;
          height: 10px;
          background: var(--gradient);
          bottom: 100%;
          left: 5px;
          right: -5px;
          transform: skew(-45deg, 0);
          transition: all 0.2s ease-in-out;
        }

        .block-cube .bg-right {
          position: absolute;
          background: var(--blue-dark);
          top: -5px;
          bottom: 5px;
          width: 10px;
          left: 100%;
          transform: skew(0, -45deg);
          transition: all 0.2s ease-in-out;
        }

        .block-cube .bg {
          position: absolute;
          left: 0;
          top: 0;
          right: 0;
          bottom: 0;
          background: var(--gradient);
          transition: all 0.2s ease-in-out;
        }

        .block-cube .bg-inner {
          background: var(--bg-input);
          position: absolute;
          left: 2px;
          top: 2px;
          right: 2px;
          bottom: 2px;
          transition: all 0.2s ease-in-out;
        }

        .block-cube-hover:hover .bg-inner {
          top: 100% !important;
        }

        .text-label {
          position: relative;
          z-index: 3;
        }

        .btn-success-3d .bg-top,
        .btn-success-3d .bg {
          background: var(--gradient-success);
        }

        .btn-success-3d .bg-inner {
          background: #28a745;
        }
      `}</style>
      
      {filteredRecs.length === 0 ? (
        <p>Aucune recommandation pour le moment. Essayez d'ajouter des articles Ã  votre wishlist !</p>
      ) : (
        <div className="row">
          {filteredRecs.map(p => (
            <div key={p._id} className="col-md-4 mb-4">
              <div className="card h-100 shadow-sm">
                <div className="card-body">
                  <span className="badge bg-primary mb-2">{p.category}</span>
                  <h5>{p.name}</h5>
                  <h6 className="text-success font-weight-bold">{p.price} â‚¬</h6>
                  <p className="text-muted small">Tags: {p.tags.join(', ')}</p>
                </div>
                <div className="card-footer bg-white border-0">
                  <div className="block-cube block-cube-hover btn-success-3d">
                    <button
                      className="btn-3d"
                      onClick={() => buy(p._id)}
                    >
                      <div className="bg-top"><div className="bg-inner"></div></div>
                      <div className="bg-right"><div className="bg-inner"></div></div>
                      <div className="bg"><div className="bg-inner"></div></div>
                      <span className="text-label">Acheter</span>
                    </button>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
