import React, { useEffect, useState } from 'react';

export default function Products({ user, onAddToCart, onAddToWishlist, wishlistItems }) {
  const [products, setProducts] = useState([]);
  const [ratings, setRatings] = useState({});
  const [loading, setLoading] = useState(true);

  // Load products
  useEffect(() => {
    fetch('http://localhost:4000/api/products')
      .then(res => res.json())
      .then(data => {
        setProducts(data || []);
        // Initialize ratings
        const initialRatings = {};
        data.forEach(p => {
          initialRatings[p._id] = p.rating || 0;
        });
        setRatings(initialRatings);
      })
      .finally(() => setLoading(false));
  }, []);

  // Check if product is in wishlist
  function isInWishlist(productId) {
    return wishlistItems.some(item => item._id === productId);
  }

  // Add rating
  async function addRating(productId, rating) {
    try {
      await fetch(`http://localhost:4000/api/products/${productId}/rate`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ rating, userId: user._id })
      });
      setRatings({
        ...ratings,
        [productId]: rating
      });
    } catch (err) {
      console.error('Erreur:', err);
    }
  }

  if (loading) {
    return <div className="text-center py-5"><p className="text-muted">Chargement des produits...</p></div>;
  }

  return (
    <div className="mt-4">
      <style>{`
        :root {
          --blue-dark: #00326f;
          --blue-light: #0151aa;
          --bg-input: #0753b0ff;
          --gradient: linear-gradient(90deg, #00326f 0%, #041830ff 100%);
          --gradient-success: linear-gradient(90deg, #28a745 0%, #20c997 100%);
          --gradient-danger: linear-gradient(90deg, #dc3545 0%, #fd7e14 100%);
          --gradient-neutral: linear-gradient(90deg, #6c757d 0%, #495057 100%);
        }

        .btn-3d {
          width: 100%;
          cursor: pointer;
          border: 0;
          background: transparent;
          color: #fff;
          padding: 10px 14px;
          font-weight: bold;
          font-size: 14px;
          text-transform: uppercase;
          letter-spacing: 0.1em;
          position: relative;
          margin-bottom: 10px;
        }

        .block-cube {
          position: relative;
        }

        .block-cube .bg-top {
          position: absolute;
          height: 10px;
          background: var(--gradient);
          bottom: 100%;
          left: 5px;
          right: -5px;
          transform: skew(-45deg, 0);
          transition: all 0.2s ease-in-out;
        }

        .block-cube .bg-right {
          position: absolute;
          background: var(--blue-dark);
          top: -5px;
          bottom: 5px;
          width: 10px;
          left: 100%;
          transform: skew(0, -45deg);
          transition: all 0.2s ease-in-out;
        }

        .block-cube .bg {
          position: absolute;
          left: 0;
          top: 0;
          right: 0;
          bottom: 0;
          background: var(--gradient);
          transition: all 0.2s ease-in-out;
        }

        .block-cube .bg-inner {
          background: var(--bg-input);
          position: absolute;
          left: 2px;
          top: 2px;
          right: 2px;
          bottom: 2px;
          transition: all 0.2s ease-in-out;
        }

        .block-cube.block-input input:focus ~ .bg-top .bg-inner,
        .block-cube.block-input input:focus ~ .bg-right .bg-inner,
        .block-cube.block-input input:focus ~ .bg .bg-inner {
          background: rgba(255, 255, 255, 0.1);
          top: 100%;
        }

        .block-cube-hover:hover .bg-inner {
          top: 100% !important;
        }

        .text-label {
          position: relative;
          z-index: 3;
        }

        .btn-success-3d .bg-top,
        .btn-success-3d .bg {
          background: var(--gradient-success);
        }

        .btn-success-3d .bg-inner {
          background: #28a745;
        }

        .btn-danger-3d .bg-top,
        .btn-danger-3d .bg {
          background: var(--gradient-danger);
        }

        .btn-danger-3d .bg-inner {
          background: #dc3545;
        }
      `}</style>
      <h3 className="mb-4">üõí Catalogue des Produits</h3>

      {products.length === 0 ? (
        <div className="text-center py-5 bg-light rounded">
          <p className="text-muted">Aucun produit disponible</p>
        </div>
      ) : (
        <div className="row">
          {products.map(p => (
            <div key={p._id} className="col-md-4 col-lg-3 mb-4">
              <div className="card h-100 shadow-sm">
                <div className="card-body">
                  <h5 className="card-title">{p.name}</h5>
                  <p className="card-text text-muted small">{p.category}</p>
                  <div className="mb-3">
                    <span className="h6 text-primary fw-bold">${p.price}</span>
                  </div>

                  {p.tags && p.tags.length > 0 && (
                    <div className="mb-3">
                      {p.tags.map(tag => (
                        <span key={tag} className="badge bg-secondary me-1 mb-1">{tag}</span>
                      ))}
                    </div>
                  )}

                  <div className="mb-3">
                    <label className="form-label small fw-bold">Note: ‚≠ê {ratings[p._id] || 0}/5</label>
                    <div className="d-flex gap-1">
                      {[1, 2, 3, 4, 5].map(star => (
                        <button
                          key={star}
                          onClick={() => addRating(p._id, star)}
                          style={{ background: 'none', border: 'none', cursor: 'pointer', fontSize: '1.5rem' }}
                          className={star <= (ratings[p._id] || 0) ? 'text-warning' : 'text-secondary'}
                        >
                          ‚≠ê
                        </button>
                      ))}
                    </div>
                  </div>

                  <div className="d-grid gap-2">
                    <div className="block-cube block-cube-hover">
                      <button
                        onClick={() => onAddToCart(p)}
                        className="btn-3d"
                      >
                        <div className="bg-top"><div className="bg-inner"></div></div>
                        <div className="bg-right"><div className="bg-inner"></div></div>
                        <div className="bg"><div className="bg-inner"></div></div>
                        <span className="text-label">üõí Ajouter au panier</span>
                      </button>
                    </div>
                    <div className={`block-cube block-cube-hover ${isInWishlist(p._id) ? 'btn-danger-3d' : 'btn-neutral-3d'}`}>
                      <button
                        onClick={() => onAddToWishlist(p)}
                        className="btn-3d"
                      >
                        <div className="bg-top"><div className="bg-inner"></div></div>
                        <div className="bg-right"><div className="bg-inner"></div></div>
                        <div className="bg"><div className="bg-inner"></div></div>
                        <span className="text-label">{isInWishlist(p._id) ? '‚ù§Ô∏è Dans ma liste' : 'ü§ç Ajouter √† ma liste'}</span>
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
