import React, { useState } from 'react';

export default function Cart({ cartItems, onRemove, onCheckout }) {
  const [loading, setLoading] = useState(false);

  const total = cartItems.reduce((sum, item) => sum + (item.price || 0), 0);

  async function handleCheckout() {
    setLoading(true);
    try {
      await onCheckout();
      alert('‚úÖ Achat r√©ussi!');
    } catch (err) {
      alert('‚ùå Erreur lors de l\'achat');
    }
    setLoading(false);
  }

  if (cartItems.length === 0) {
    return (
      <div style={{ minHeight: '100vh' }} className="bg-light pt-5">
        <div className="container">
          <div className="card shadow-lg p-5 text-center">
            <h2 className="h3 fw-bold text-dark mb-3">üõí Votre panier est vide</h2>
            <p className="text-muted">Commencez √† faire du shopping!</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div style={{ minHeight: '100vh' }} className="bg-light pt-5 pb-5">
      <div className="container">
        <h2 className="h3 fw-bold text-dark mb-4">üõí Votre Panier</h2>

        {/* Cart Items */}
        <div className="card shadow-lg mb-4">
          {cartItems.map((item) => (
            <div key={item._id} className="d-flex justify-content-between align-items-center p-4 border-bottom">
              <div className="flex-grow-1">
                <h5 className="fw-bold text-dark mb-2">{item.name}</h5>
                <p className="text-muted small mb-2">{item.category}</p>
                <p className="text-primary fw-bold">${item.price}</p>
              </div>
              <button
                onClick={() => onRemove(item._id)}
                className="btn btn-danger btn-sm"
              >
                Supprimer
              </button>
            </div>
          ))}
        </div>

        {/* Summary */}
        <div className="card shadow-lg p-4">
          <div className="d-flex justify-content-between align-items-center mb-4">
            <span className="h5 fw-bold">Total:</span>
            <span className="h4 fw-bold text-primary">${total.toFixed(2)}</span>
          </div>

          <button
            onClick={handleCheckout}
            disabled={loading}
            className="btn btn-success btn-lg w-100 fw-bold"
          >
            {loading ? 'Traitement...' : '‚úÖ Proc√©der au paiement'}
          </button>
        </div>
      </div>
    </div>
  );
}
