import React, { useEffect, useState } from 'react';

export default function Recommendations({ user }) {
  const [recs, setRecs] = useState([]);
  const [loading, setLoading] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState('All');

  useEffect(() => {
    const fetchRecs = async () => {
      if (!user || !user._id) return;
      
      setLoading(true);
      try {
        const response = await fetch(`http://localhost:4000/api/recommend/${user._id}`);
        const data = await response.json();
        setRecs(data); 
      } catch (error) {
        console.error("Erreur lors du chargement des recommandations:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchRecs();
  }, [user._id]);

  async function buy(productId) {
    try {
      const response = await fetch('http://localhost:4000/api/purchase', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ userId: user._id, productId })
      });
      if (response.ok) {
        alert('Produit achetÃ© ! Vos recommandations vont bientÃ´t se mettre Ã  jour.');
      }
    } catch (error) {
      alert("Erreur lors de l'achat");
    }
  }

  // Get unique categories
  const categories = ['All', ...new Set(recs.map(p => p.category))];

  // Filter recommendations
  const filteredRecs = selectedCategory === 'All' ? recs : recs.filter(p => p.category === selectedCategory);

  if (loading) return (
    <div style={{ minHeight: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center', backgroundColor: '#ffffffff', fontFamily: 'Monospace, serif' }}>
      <p>Chargement des recommandations...</p>
    </div>
  );

  return (
    <div style={{ minHeight: '100vh', backgroundColor: '#ffffffff', padding: '20px', fontFamily: 'Monospace, serif' }}>
      <style>{`
        :root {
          --blue-dark: #00326f;
          --blue-light: #0151aa;
          --gradient: linear-gradient(90deg, #00326f 0%, #041830ff 100%);
          --bg-card: #fff;
        }

        .recommendations-container {
          maxWidth: '1200px';
          margin: '0 auto';
          padding: '40px 20px';
        }

        .title {
          color: var(--blue-dark);
          textAlign: 'center';
          marginBottom: '30px';
          fontSize: '28px';
          fontWeight: 'bold';
          textTransform: 'uppercase';
          letterSpacing: '2px';
        }

        .filter-section {
          textAlign: 'center';
          marginBottom: '40px';
        }

        .filter-select {
          padding: '10px 15px';
          border: '2px solid var(--blue-dark)';
          borderRadius: '5px';
          backgroundColor: 'var(--bg-card)';
          color: 'var(--blue-dark)';
          fontSize: '16px';
          fontFamily: 'Monospace, serif';
          cursor: 'pointer';
        }

        .products-grid {
          display: 'grid';
          gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))';
          gap: '20px';
        }

        .product-card {
          background: var(--bg-card);
          borderRadius: '10px';
          boxShadow: '0 10px 30px rgba(0, 50, 111, 0.3)';
          overflow: 'hidden';
          transition: 'transform 0.3s ease, box-shadow 0.3s ease';
        }

        .product-card:hover {
          transform: 'translateY(-5px)';
          boxShadow: '0 15px 40px rgba(0, 50, 111, 0.4)';
        }

        .card-header {
          background: var(--gradient);
          color: '#fff';
          padding: '15px';
          textAlign: 'center';
          fontWeight: 'bold';
        }

        .card-body {
          padding: '20px';
        }

        .product-name {
          color: var(--blue-dark);
          marginBottom: '10px';
          fontSize: '18px';
        }

        .product-price {
          color: '#28a745';
          fontWeight: 'bold';
          fontSize: '16px';
          marginBottom: '10px';
        }

        .product-tags {
          color: '#6c757d';
          fontSize: '14px';
          marginBottom: '15px';
        }

        .buy-btn {
          width: '100%';
          padding: '10px';
          background: var(--gradient);
          color: '#fff';
          border: 'none';
          borderRadius: '5px';
          cursor: 'pointer';
          fontSize: '16px';
          fontWeight: 'bold';
          textTransform: 'uppercase';
          letterSpacing: '1px';
          transition: 'background 0.3s ease';
        }

        .buy-btn:hover {
          background: 'linear-gradient(90deg, #041830ff 0%, #00326f 100%)';
        }

        .no-recs {
          textAlign: 'center';
          color: var(--blue-dark);
          fontSize: '18px';
        }
      `}</style>

      <div className="recommendations-container">
        <h1 className="title">ðŸŽ¯ Recommandations pour {user.firstName}</h1>
        
        <div className="filter-section">
          <label htmlFor="category-filter" style={{ marginRight: '10px', color: 'var(--blue-dark)', fontWeight: 'bold' }}>Filtrer par catÃ©gorie:</label>
          <select
            id="category-filter"
            className="filter-select"
            value={selectedCategory}
            onChange={(e) => setSelectedCategory(e.target.value)}
          >
            {categories.map(cat => (
              <option key={cat} value={cat}>{cat}</option>
            ))}
          </select>
        </div>
        
        {filteredRecs.length === 0 ? (
          <p className="no-recs">Aucune recommandation pour le moment. Essayez d'ajouter des articles Ã  votre wishlist !</p>
        ) : (
          <div className="products-grid">
            {filteredRecs.map(p => (
              <div key={p._id} className="product-card">
                <div className="card-header">{p.category}</div>
                <div className="card-body">
                  <h3 className="product-name">{p.name}</h3>
                  <p className="product-price">{p.price} â‚¬</p>
                  <p className="product-tags">Tags: {p.tags.join(', ')}</p>
                  <button className="buy-btn" onClick={() => buy(p._id)}>
                    Acheter
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
