import React from 'react';
import { useNavigate } from 'react-router-dom';
import iconeIcon from './icone.png';
import heartIcon from './heart.png';
import panierIcon from './panier.png';
import boxIcon from './box.png';

export default function Navbar({ user, cartCount, onLogout }) {
  const navigate = useNavigate();

  return (
    <>
      {/* Styles CSS pour le bouton 3D de déconnexion */}
      <style>{`
        :root {
          --blue-dark: #00326f;
          --blue-light: #0151aa;
          --bg-input: #0753b0;
          --gradient: linear-gradient(90deg, #00326f 0%, #041830 100%);
          --gradient-danger: linear-gradient(90deg, #dc3545 0%, #fd7e14 100%);
        }

        .btn-3d {
          cursor: pointer;
          border: 0;
          background: transparent;
          color: #fff;
          padding: 8px 16px;
          font-weight: bold;
          font-size: 14px;
          text-transform: uppercase;
          letter-spacing: 0.1em;
          position: relative;
          border-radius: 6px;
        }

        .block-cube {
          position: relative;
          display: inline-block;
        }

        .block-cube .bg-top {
          position: absolute;
          height: 10px;
          background: var(--gradient);
          bottom: 100%;
          left: 5px;
          right: -5px;
          transform: skew(-45deg, 0);
          transition: all 0.3s ease;
        }

        .block-cube .bg-right {
          position: absolute;
          background: var(--blue-dark);
          top: -5px;
          bottom: 5px;
          width: 10px;
          left: 100%;
          transform: skew(0, -45deg);
          transition: all 0.3s ease;
        }

        .block-cube .bg {
          position: absolute;
          inset: 0;
          background: var(--gradient);
          transition: all 0.3s ease;
          border-radius: 6px;
        }

        .block-cube .bg-inner {
          background: var(--bg-input);
          position: absolute;
          inset: 2px;
          border-radius: 4px;
          transition: all 0.3s ease;
        }

        .block-cube-hover:hover .bg-inner {
          top: 100% !important;
        }

        .text-label {
          position: relative;
          z-index: 3;
        }

        .btn-danger-3d .bg-top,
        .btn-danger-3d .bg {
          background: var(--gradient-danger);
        }

        .btn-danger-3d .bg-inner {
          background: #dc3545;
        }

        .btn-danger-3d .bg-right {
          background: #c82333;
        }
      `}</style>

      <nav className="navbar navbar-expand-lg navbar-light bg-white shadow-sm sticky-top">
        <div className="container-fluid px-4">
          {/* Logo / Brand */}
          <button
            className="navbar-brand btn btn-link text-decoration-none d-flex align-items-center"
            onClick={() => navigate('/')}
          >
            <img
              src={iconeIcon}
              alt="Logo"
              style={{ width: '32px', height: '32px', marginRight: '10px' }}
            />
            <span className="fw-bold text-primary" style={{ fontSize: '1.5rem' }}>
              RECO
            </span>
          </button>

          {/* Toggle pour mobile */}
          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarNav"
            aria-controls="navbarNav"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>

          {/* Menu */}
          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav ms-auto align-items-center">
              <li className="nav-item">
                <button
                  onClick={() => navigate('/')}
                  className="nav-link btn btn-link text-dark fw-medium"
                >
                  Accueil
                </button>
              </li>

              <li className="nav-item">
                <button
                  onClick={() => navigate('/wishlist')}
                  className="nav-link btn btn-link text-dark fw-medium d-flex align-items-center"
                >
                  <img src={heartIcon} alt="Wishlist" style={{ width: '20px', height: '20px', marginRight: '8px' }} />
                  Liste de souhaits
                </button>
              </li>

              <li className="nav-item">
                <button
                  onClick={() => navigate('/cart')}
                  className="nav-link btn btn-link text-dark fw-medium position-relative d-flex align-items-center"
                >
                  <img src={panierIcon} alt="Panier" style={{ width: '20px', height: '20px', marginRight: '8px' }} />
                  Panier
                  {cartCount > 0 && (
                    <span className="position-absolute top-0 start-100 translate-middle badge rounded-pill bg-danger">
                      {cartCount}
                      <span className="visually-hidden">articles dans le panier</span>
                    </span>
                  )}
                </button>
              </li>

              <li className="nav-item">
                <button
                  onClick={() => navigate('/purchases')}
                  className="nav-link btn btn-link text-dark fw-medium d-flex align-items-center"
                >
                  <img src={boxIcon} alt="Achats" style={{ width: '20px', height: '20px', marginRight: '8px' }} />
                  Mes achats
                </button>
              </li>

              {/* Salutation utilisateur */}
              <li className="nav-item mx-4">
                <span className="text-dark">
                  Bonjour, <span className="text-primary fw-bold">{user?.firstName || 'Utilisateur'}</span>
                </span>
              </li>

              {/* Bouton Déconnexion avec effet 3D */}
              <li className="nav-item">
                <div className="block-cube block-cube-hover btn-danger-3d">
                  <button onClick={onLogout} className="btn-3d">
                    <div className="bg-top"><div className="bg-inner"></div></div>
                    <div className="bg-right"><div className="bg-inner"></div></div>
                    <div className="bg"><div className="bg-inner"></div></div>
                    <span className="text-label">Déconnexion</span>
                  </button>
                </div>
              </li>
            </ul>
          </div>
        </div>
      </nav>
    </>
  );
}
