// This is the main App component for the React frontend, handling routing and user state.

// Import React hooks and routing components.
import React, { useState } from 'react';
import { BrowserRouter, Routes, Route, useNavigate } from 'react-router-dom';

// Import custom components.
import Login from './Login';
import Products from './Products';
import Recommendations from './Recommendations';
import Navbar from './Navbar';
import Cart from './Cart';
import Wishlist from './Wishlist';
import Purchases from './Purchases';

// Home component: Displays welcome message and navigation to recommendations, plus products list.
function Home({ user, cartItems, wishlistItems, onAddToCart, onAddToWishlist, onRemoveFromWishlist }) {
  const navigate = useNavigate(); // Hook to programmatically navigate.

  return (
    <div style={{ minHeight: '100vh' }} className="bg-light">
      <div className="container py-4">
        {/* Welcome Section */}
        <div className="bg-primary text-white rounded shadow-lg p-4 mb-4">
          <h1 className="h3 fw-bold mb-2">Bienvenue {user.firstName}! ðŸ‘‹</h1>
          <p className="mb-3">DÃ©couvrez nos produits recommandÃ©s spÃ©cialement pour vous</p>
          <button
            className="btn btn-light fw-bold"
            onClick={() => navigate('/recommendations')}
          >
            ðŸŽ¯ Obtenir des recommandations
          </button>
        </div>

        {/* Products Section */}
        <Products
          user={user}
          onAddToCart={onAddToCart}
          onAddToWishlist={onAddToWishlist}
          wishlistItems={wishlistItems}
        />
      </div>
    </div>
  );
}

// Main App component: Manages user authentication state and routing.
export default function App() {
  const [user, setUser] = useState(null);
  const [cartItems, setCartItems] = useState([]);
  const [wishlistItems, setWishlistItems] = useState([]);

  // Add product to cart
  function handleAddToCart(product) {
    setCartItems([...cartItems, product]);
    
    // Save to API
    if (user) {
      fetch('http://localhost:4000/api/cart/add', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          userId: user._id,
          productId: product._id,
          quantity: 1
        })
      }).catch(err => console.error('Cart API error:', err));
    }
    
    alert(`âœ… ${product.name} ajoutÃ© au panier!`);
  }

  // Add product to wishlist
  function handleAddToWishlist(product) {
    const isInWishlist = wishlistItems.some(item => item._id === product._id);
    
    if (isInWishlist) {
      setWishlistItems(wishlistItems.filter(item => item._id !== product._id));
      
      // Remove from API
      if (user) {
        fetch('http://localhost:4000/api/wishlist/remove', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            userId: user._id,
            productId: product._id
          })
        }).catch(err => console.error('Wishlist API error:', err));
      }
      
      alert(`â¤ï¸ ${product.name} retirÃ© de votre liste`);
    } else {
      setWishlistItems([...wishlistItems, product]);
      
      // Add to API
      if (user) {
        fetch('http://localhost:4000/api/wishlist/add', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            userId: user._id,
            productId: product._id
          })
        }).catch(err => console.error('Wishlist API error:', err));
      }
      
      alert(`â¤ï¸ ${product.name} ajoutÃ© Ã  votre liste!`);
    }
  }

  // Remove from cart
  function handleRemoveFromCart(productId) {
    setCartItems(cartItems.filter(item => item._id !== productId));
  }

  // Remove from wishlist
  function handleRemoveFromWishlist(productId) {
    setWishlistItems(wishlistItems.filter(item => item._id !== productId));
  }

  // Checkout
  async function handleCheckout() {
    try {
      for (const item of cartItems) {
        await fetch('http://localhost:4000/api/purchase', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            userId: user._id,
            productId: item._id
          })
        });
      }
      setCartItems([]); // Clear cart
      alert('âœ… Achat rÃ©ussi!');
    } catch (err) {
      throw new Error('Erreur lors de l\'achat');
    }
  }

  // If no user is logged in, show the Login component.
  if (!user) return <Login onLogin={setUser} />;

  // If user is logged in, render the app with routing.
  return (
    <BrowserRouter>
      <Navbar user={user} cartCount={cartItems.length} onLogout={() => setUser(null)} />
      <Routes>
        <Route
          path="/"
          element={
            <Home
              user={user}
              cartItems={cartItems}
              wishlistItems={wishlistItems}
              onAddToCart={handleAddToCart}
              onAddToWishlist={handleAddToWishlist}
              onRemoveFromWishlist={handleRemoveFromWishlist}
            />
          }
        />
        <Route
          path="/recommendations"
          element={<Recommendations user={user} />}
        />
        <Route
          path="/cart"
          element={
            <Cart
              cartItems={cartItems}
              onRemove={handleRemoveFromCart}
              onCheckout={handleCheckout}
            />
          }
        />
        <Route
          path="/wishlist"
          element={
            <Wishlist
              wishlistItems={wishlistItems}
              onRemove={handleRemoveFromWishlist}
            />
          }
        />
        <Route path="/purchases" element={<Purchases user={user} />} />
      </Routes>
    </BrowserRouter>
  );
}
