// This is the main App component for the React frontend, handling routing and user state.

// Import React hooks and routing components.
import React, { useState } from 'react';
import { BrowserRouter, Routes, Route, useNavigate } from 'react-router-dom';

// Import custom components.
import Login from './Login';
import Products from './Products';
import Recommendations from './Recommendations';

// Home component: Displays welcome message and navigation to recommendations, plus products list.
function Home({ user }) {
  const navigate = useNavigate(); // Hook to programmatically navigate.

  return (
    <div className="container mt-4">
      <div className="card shadow p-4 mb-4">
        <h4>Bienvenue {user.firstName} ðŸ‘‹</h4> {/* Welcome message with user's first name. */}
        <button
          className="btn btn-success mt-3"
          onClick={() => navigate('/recommendations')} // Navigate to recommendations page.
        >
          ðŸŽ¯ Obtenir des recommandations
        </button>
      </div>

      <Products user={user} /> {/* Render the Products component. */}
    </div>
  );
}

// Main App component: Manages user authentication state and routing.
export default function App() {
  const [user, setUser] = useState(null); // State for logged-in user.

  // If no user is logged in, show the Login component.
  if (!user) return <Login onLogin={setUser} />;

  // If user is logged in, render the app with routing.
  return (
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<Home user={user} />} /> {/* Home page. */}
        <Route path="/recommendations" element={<Recommendations user={user} />} /> {/* Recommendations page. */}
      </Routes>
    </BrowserRouter>
  );
}
