// Recommendations component: fetches personalized recommendations for the logged-in user
// and displays them similarly to the product catalog. Purchases can be triggered from here.
import React, { useEffect, useState } from 'react';

export default function Recommendations({ user }) {
  const [recs, setRecs] = useState([]);

  // Fetch recommendations when the component mounts or when `user` changes
  useEffect(() => {
    fetch(`http://localhost:4000/api/recommend/${user._id}`)
      .then(res => res.json())
      .then(setRecs);
  }, [user]);

  // Buy a recommended product (records purchase in backend)
  async function buy(productId) {
    await fetch('http://localhost:4000/api/purchase', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ userId: user._id, productId })
    });
    alert('Produit achetÃ©');
  }

  // Render recommended products as cards
  return (
    <div className="container mt-4">
      <h3 className="mb-4">ðŸŽ¯ Recommandations pour vous</h3>

      <div className="row">
        {recs.map(p => (
          <div key={p._id} className="col-md-4 mb-4">
            <div className="card h-100 shadow">
              <div className="card-body">
                <h5>{p.name}</h5>
                <p>{p.category}</p>
                <h6 className="text-success">${p.price}</h6>
              </div>
              <div className="card-footer bg-white border-0">
                <button
                  className="btn btn-success w-100"
                  onClick={() => buy(p._id)}
                >
                  Acheter
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
