// Login component: handles user authentication form and calls backend API.
// This is intentionally simple: it sends `{ email, password }` to the backend
// and expects a user object in response. For production, use secure flows.
import React, { useState } from 'react';

export default function Login({ onLogin }) {
  // Local state for the form
  const [email, setEmail] = useState('aya@test.com');
  const [password, setPassword] = useState('1234');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  // Send credentials to backend and invoke `onLogin` with returned user
  async function login() {
    setLoading(true);
    try {
      const res = await fetch('http://localhost:4000/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password })
      });
      if (!res.ok) throw new Error();
      const user = await res.json();
      onLogin(user); // Propagate user to parent App component
    } catch {
      // Show a friendly error message on failure
      setError('Email ou mot de passe incorrect');
    }
    setLoading(false);
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-600 to-blue-800 flex items-center justify-center p-4">
      <div className="bg-white rounded-lg shadow-2xl p-8 w-full max-w-md">
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold text-blue-600 mb-2">üõçÔ∏è RECO</h1>
          <p className="text-gray-600">Recommandations Intelligentes</p>
        </div>

        <h2 className="text-2xl font-bold text-gray-800 text-center mb-6">Se Connecter</h2>

        <div className="space-y-4">
          <input
            type="email"
            className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-600 focus:ring-2 focus:ring-blue-200"
            placeholder="Email"
            value={email}
            onChange={e => setEmail(e.target.value)}
          />

          <input
            type="password"
            className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-600 focus:ring-2 focus:ring-blue-200"
            placeholder="Mot de passe"
            value={password}
            onChange={e => setPassword(e.target.value)}
          />

          <button
            onClick={login}
            disabled={loading}
            className="w-full px-6 py-3 bg-blue-600 text-white font-bold rounded-lg hover:bg-blue-700 transition disabled:bg-gray-400"
          >
            {loading ? 'Connexion...' : 'üîê Se connecter'}
          </button>
        </div>

        {error && (
          <div className="mt-4 p-4 bg-red-100 border border-red-400 text-red-700 rounded-lg">
            ‚ùå {error}
          </div>
        )}

        <div className="mt-6 p-4 bg-blue-50 rounded-lg text-sm text-gray-600">
          <p className="font-semibold mb-2">üß™ Test Credentials:</p>
          <p>Email: <code className="bg-gray-200 px-2 py-1 rounded">aya@test.com</code></p>
          <p>Password: <code className="bg-gray-200 px-2 py-1 rounded">1234</code></p>
        </div>
      </div>
    </div>
  );
}
