import React, { useState } from 'react';
import panierIcon from './panier.png';

export default function Cart({ cartItems, onRemove, onCheckout }) {
  const [loading, setLoading] = useState(false);

  const total = cartItems.reduce((sum, item) => sum + (item.price || 0), 0);

  async function handleCheckout() {
    setLoading(true);
    try {
      await onCheckout();
      alert('✅ Achat réussi!');
    } catch (err) {
      alert('❌ Erreur lors de l\'achat');
    }
    setLoading(false);
  }

  if (cartItems.length === 0) {
    return (
      <div style={{ minHeight: '100vh' }} className="bg-light pt-5">
        <div className="container">
          <div className="card shadow-lg p-5 text-center">
            <h2 className="h3 fw-bold text-dark mb-3"><img src={panierIcon} alt="Cart" style={{ width: '24px', height: '24px', marginRight: '8px' }} />Votre panier est vide</h2>
            <p className="text-muted">Commencez à faire du shopping!</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div style={{ minHeight: '100vh' }} className="bg-light pt-5 pb-5">
      <style>{`
        .btn-3d {
          width: 100%;
          cursor: pointer;
          border: 0;
          background: transparent;
          color: #fff;
          padding: 14px;
          font-weight: bold;
          font-size: 16px;
          text-transform: uppercase;
          letter-spacing: 0.1em;
          position: relative;
        }

        .block-cube {
          position: relative;
        }

        .block-cube .bg-top {
          position: absolute;
          height: 10px;
          background: linear-gradient(90deg, #00326f 0%, #041830ff 100%);
          bottom: 100%;
          left: 5px;
          right: -5px;
          transform: skew(-45deg, 0);
          transition: all 0.2s ease-in-out;
        }

        .block-cube .bg-right {
          position: absolute;
          background: #00326f;
          top: -5px;
          bottom: 5px;
          width: 10px;
          left: 100%;
          transform: skew(0, -45deg);
          transition: all 0.2s ease-in-out;
        }

        .block-cube .bg {
          position: absolute;
          left: 0;
          top: 0;
          right: 0;
          bottom: 0;
          background: linear-gradient(90deg, #00326f 0%, #041830ff 100%);
          transition: all 0.2s ease-in-out;
        }

        .block-cube .bg-inner {
          background: #0753b0ff;
          position: absolute;
          left: 2px;
          top: 2px;
          right: 2px;
          bottom: 2px;
          transition: all 0.2s ease-in-out;
        }

        .block-cube-hover:hover .bg-inner {
          top: 100% !important;
        }

        .text-label {
          position: relative;
          z-index: 3;
        }

        /* For danger button */
        .btn-danger-3d .bg-top,
        .btn-danger-3d .bg,
        .btn-danger-3d .bg-right {
          background: linear-gradient(90deg, #dc3545 0%, #b02a37 100%);
        }

        .btn-danger-3d .bg-inner {
          background: #a02622;
        }

        /* For success button */
        .btn-success-3d .bg-top,
        .btn-success-3d .bg,
        .btn-success-3d .bg-right {
          background: linear-gradient(90deg, #28a745 0%, #1e7e34 100%);
        }

        .btn-success-3d .bg-inner {
          background: #155724;
        }

        /* 3D Card effect */
        .card-3d {
          transition: transform 0.3s ease, box-shadow 0.3s ease;
          box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
        }

        .card-3d:hover {
          transform: translateY(-10px) rotateX(5deg);
          box-shadow: 0 15px 30px rgba(0, 0, 0, 0.2);
        }
      `}</style>
      <div className="container">
        <h2 className="h3 fw-bold text-dark mb-4"><img src={panierIcon} alt="Cart" style={{ width: '24px', height: '24px', marginRight: '8px' }} />Votre Panier</h2>

        {/* Cart Items */}
        <div className="card shadow-lg mb-4 card-3d">
          {cartItems.map((item) => (
            <div key={item._id} className="d-flex justify-content-between align-items-center p-4 border-bottom">
              <div className="flex-grow-1">
                <h5 className="fw-bold text-dark mb-2">{item.name}</h5>
                <p className="text-muted small mb-2">{item.category}</p>
                <p className="text-primary fw-bold">${item.price}</p>
              </div>
              <button
                onClick={() => onRemove(item._id)}
                className="btn-3d block-cube block-cube-hover btn-danger-3d"
                style={{ width: 'auto', padding: '8px 12px', fontSize: '14px' }}
              >
                <div className="bg-top"><div className="bg-inner"></div></div>
                <div className="bg-right"><div className="bg-inner"></div></div>
                <div className="bg"><div className="bg-inner"></div></div>
                <span className="text-label">Supprimer</span>
              </button>
            </div>
          ))}
        </div>

        {/* Summary */}
        <div className="card shadow-lg p-4 card-3d">
          <div className="d-flex justify-content-between align-items-center mb-4">
            <span className="h5 fw-bold">Total:</span>
            <span className="h4 fw-bold text-primary">${total.toFixed(2)}</span>
          </div>

          <button
            onClick={handleCheckout}
            disabled={loading}
            className="btn-3d block-cube block-cube-hover btn-success-3d"
          >
            <div className="bg-top"><div className="bg-inner"></div></div>
            <div className="bg-right"><div className="bg-inner"></div></div>
            <div className="bg"><div className="bg-inner"></div></div>
            <span className="text-label">{loading ? 'Traitement...' : '✅ Procéder au paiement'}</span>
          </button>
        </div>
      </div>
    </div>
  );
}
