import React, { useEffect, useState } from 'react';

export default function Products({ user }) {
  const [products, setProducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [loading, setLoading] = useState(false);

  // Load products
  useEffect(() => {
    fetch('http://localhost:4000/api/products')
      .then(res => res.json())
      .then(setProducts);
  }, []);

  // Confirm purchase
  async function confirmPurchase() {
    if (!selectedProduct) return;

    setLoading(true);
    await fetch('http://localhost:4000/api/purchase', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        userId: user._id,
        productId: selectedProduct._id
      })
    });

    setLoading(false);
    setSelectedProduct(null);
    alert('‚úÖ Achat confirm√©');
  }

  return (
    <div>
      <h3 className="mb-4">üõí Catalogue des produits</h3>

      <div className="row">
        {products.map(p => (
          <div key={p._id} className="col-md-4 mb-4">
            <div className="card h-100 shadow-sm">
              <div className="card-body">
                <h5 className="card-title">{p.name}</h5>
                <p className="card-text text-muted">{p.category}</p>
                <h6 className="text-primary fw-bold">${p.price}</h6>
              </div>
              <div className="card-footer bg-white border-0">
                <button
                  className="btn btn-outline-primary w-100"
                  onClick={() => setSelectedProduct(p)}
                >
                  Acheter
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* MODAL CONFIRMATION */}
      {selectedProduct && (
        <div className="modal fade show d-block" style={{ background: 'rgba(0,0,0,0.6)' }}>
          <div className="modal-dialog modal-lg modal-dialog-centered">
            <div className="modal-content shadow-lg rounded-4">

              <div className="modal-header">
                <h5 className="modal-title">Confirmation d‚Äôachat</h5>
                <button className="btn-close" onClick={() => setSelectedProduct(null)} />
              </div>

              <div className="modal-body">
                <h4 className="mb-3">{selectedProduct.name}</h4>

                <p><strong>Cat√©gorie :</strong> {selectedProduct.category}</p>
                <p><strong>Prix :</strong> <span className="text-primary fw-bold">${selectedProduct.price}</span></p>

                {selectedProduct.tags && (
                  <div className="mb-3">
                    {selectedProduct.tags.map(t => (
                      <span key={t} className="badge bg-secondary me-2">{t}</span>
                    ))}
                  </div>
                )}

                <div className="alert alert-warning mt-4">
                  ‚ö†Ô∏è <strong>Important :</strong> toute vente est d√©finitive et non remboursable.
                </div>
              </div>

              <div className="modal-footer">
                <button
                  className="btn btn-secondary"
                  onClick={() => setSelectedProduct(null)}
                >
                  Annuler
                </button>

                <button
                  className="btn btn-success"
                  onClick={confirmPurchase}
                  disabled={loading}
                >
                  {loading ? 'Traitement...' : 'Confirmer l‚Äôachat'}
                </button>
              </div>

            </div>
          </div>
        </div>
      )}
    </div>
  );
}
