import React, { useState, useEffect } from 'react';
import boxIcon from './box.png';

export default function Purchases({ user }) {
  const [purchases, setPurchases] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchPurchases();
  }, []);

  async function fetchPurchases() {
    try {
      const res = await fetch(`http://localhost:4000/api/purchase/${user._id}`);
      const data = await res.json();
      setPurchases(data || []);
    } catch (err) {
      console.error('Erreur:', err);
    }
    setLoading(false);
  }

  if (loading) {
    return (
      <div style={{ minHeight: '100vh' }} className="bg-light pt-5 d-flex align-items-center justify-content-center">
        <div className="text-muted">Chargement...</div>
      </div>
    );
  }

  if (purchases.length === 0) {
    return (
      <div style={{ minHeight: '100vh' }} className="bg-light pt-5">
        <div className="container">
          <div className="card shadow-lg p-5 text-center card-3d">
            <h2 className="h3 fw-bold text-dark mb-3"><img src={boxIcon} alt="Purchases" style={{ width: '24px', height: '24px', marginRight: '8px' }} />Aucun achat</h2>
            <p className="text-muted">Vous n\'avez pas encore achet√© d\'articles</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div style={{ minHeight: '100vh' }} className="bg-light pt-5 pb-5">
      <style>{`
        /* 3D Card effect */
        .card-3d {
          transition: transform 0.3s ease, box-shadow 0.3s ease;
          box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
        }

        .card-3d:hover {
          transform: translateY(-10px) rotateX(5deg);
          box-shadow: 0 15px 30px rgba(0, 0, 0, 0.2);
        }
      `}</style>
      <div className="container">
        <h2 className="h3 fw-bold text-dark mb-4"><img src={boxIcon} alt="Purchases" style={{ width: '24px', height: '24px', marginRight: '8px' }} />Historique des Achats</h2>

        <div className="space-y-3">
          {purchases.map((purchase) => (
            <div key={purchase._id} className="card shadow-sm p-4 mb-3 card-3d">
              <div className="d-flex justify-content-between align-items-start">
                <div>
                  <h5 className="fw-bold text-dark mb-2">
                    {purchase.product_id?.name || 'Produit supprim√©'}
                  </h5>
                  <p className="text-muted small mb-2">
                    <span className="fw-bold">Cat√©gorie:</span> {purchase.product_id?.category}
                  </p>
                  <p className="text-primary fw-bold">
                    üí∞{purchase.product_id?.price || 'N/A'}
                  </p>
                </div>
                <div className="text-end">
                  <p className="text-muted small mb-2">
                    {new Date(purchase.timestamp).toLocaleDateString()}
                  </p>
                  <span className="badge bg-success">‚úÖ Achet√©</span>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
