import React, { useEffect, useState } from 'react';
import panierIcon from './panier.png';

export default function Products({ user, onAddToCart, onAddToWishlist, wishlistItems }) {
  const [products, setProducts] = useState([]);
  const [ratings, setRatings] = useState({});
  const [loading, setLoading] = useState(true);

  // Load products
  useEffect(() => {
    fetch('http://localhost:4000/api/products')
      .then(res => res.json())
      .then(data => {
        setProducts(data || []);
        // Initialize ratings
        const initialRatings = {};
        data.forEach(p => {
          initialRatings[p._id] = p.rating || 0;
        });
        setRatings(initialRatings);
      })
      .finally(() => setLoading(false));
  }, []);

  // Check if product is in wishlist
  function isInWishlist(productId) {
    return wishlistItems.some(item => item._id === productId);
  }

  // Add rating
  async function addRating(productId, rating) {
    try {
      await fetch(`http://localhost:4000/api/products/${productId}/rate`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ rating, userId: user._id })
      });
      setRatings({
        ...ratings,
        [productId]: rating
      });
    } catch (err) {
      console.error('Erreur:', err);
    }
  }

  if (loading) {
    return <div className="text-center py-5"><p className="text-muted">Chargement des produits...</p></div>;
  }

  return (
    <div className="mt-4">
      <style>{`
        .btn-3d {
          cursor: pointer;
          border: 0;
          background: transparent;
          color: #fff;
          padding: 10px 12px;
          font-weight: bold;
          font-size: 14px;
          text-transform: uppercase;
          letter-spacing: 0.1em;
          position: relative;
        }

        .block-cube {
          position: relative;
        }

        .block-cube .bg-top {
          position: absolute;
          height: 8px;
          background: linear-gradient(90deg, #00326f 0%, #041830ff 100%);
          bottom: 100%;
          left: 3px;
          right: -3px;
          transform: skew(-45deg, 0);
          transition: all 0.2s ease-in-out;
        }

        .block-cube .bg-right {
          position: absolute;
          background: #00326f;
          top: -3px;
          bottom: 3px;
          width: 8px;
          left: 100%;
          transform: skew(0, -45deg);
          transition: all 0.2s ease-in-out;
        }

        .block-cube .bg {
          position: absolute;
          left: 0;
          top: 0;
          right: 0;
          bottom: 0;
          background: linear-gradient(90deg, #00326f 0%, #041830ff 100%);
          transition: all 0.2s ease-in-out;
        }

        .block-cube .bg-inner {
          background: #0753b0ff;
          position: absolute;
          left: 2px;
          top: 2px;
          right: 2px;
          bottom: 2px;
          transition: all 0.2s ease-in-out;
        }

        .block-cube-hover:hover .bg-inner {
          top: 100% !important;
        }

        .text-label {
          position: relative;
          z-index: 3;
        }

        /* For secondary button */
        .btn-secondary-3d .bg-top,
        .btn-secondary-3d .bg,
        .btn-secondary-3d .bg-right {
          background: linear-gradient(90deg, #6c757d 0%, #545b62 100%);
        }

        .btn-secondary-3d .bg-inner {
          background: #495057;
        }

        /* For danger button */
        .btn-danger-3d .bg-top,
        .btn-danger-3d .bg,
        .btn-danger-3d .bg-right {
          background: linear-gradient(90deg, #dc3545 0%, #b02a37 100%);
        }

        .btn-danger-3d .bg-inner {
          background: #a02622;
        }

        /* 3D Card effect */
        .card-3d {
          transition: transform 0.3s ease, box-shadow 0.3s ease;
          box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
        }

        .card-3d:hover {
          transform: translateY(-10px) rotateX(5deg);
          box-shadow: 0 15px 30px rgba(0, 0, 0, 0.2);
        }
      `}</style>
      <h3 className="mb-4"><img src={panierIcon} alt="Products" style={{ width: '24px', height: '24px', marginRight: '8px' }} />Catalogue des Produits</h3>

      {products.length === 0 ? (
        <div className="text-center py-5 bg-light rounded card-3d" style={{ padding: '20px' }}>
          <p className="text-muted">Aucun produit disponible</p>
        </div>
      ) : (
        <div className="row">
          {products.map(p => (
            <div key={p._id} className="col-md-4 col-lg-3 mb-4">
              <div className="card h-100 shadow-sm card-3d">
                <div className="card-body">
                  <h5 className="card-title">{p.name}</h5>
                  <p className="card-text text-muted small">{p.category}</p>
                  <div className="mb-3">
                    <span className="h6 text-primary fw-bold">${p.price}</span>
                  </div>

                  {p.tags && p.tags.length > 0 && (
                    <div className="mb-3">
                      {p.tags.map(tag => (
                        <span key={tag} className="badge bg-secondary me-1 mb-1">{tag}</span>
                      ))}
                    </div>
                  )}

                  <div className="mb-3">
                    <label className="form-label small fw-bold">Note: ‚≠ê {ratings[p._id] || 0}/5</label>
                    <div className="d-flex gap-1">
                      {[1, 2, 3, 4, 5].map(star => (
                        <button
                          key={star}
                          onClick={() => addRating(p._id, star)}
                          style={{ background: 'none', border: 'none', cursor: 'pointer', fontSize: '1.5rem' }}
                          className={star <= (ratings[p._id] || 0) ? 'text-warning' : 'text-secondary'}
                        >
                          ‚≠ê
                        </button>
                      ))}
                    </div>
                  </div>

                  <div className="d-grid gap-2">
                    <button
                      onClick={() => onAddToCart(p)}
                      className="btn-3d block-cube block-cube-hover"
                    >
                      <div className="bg-top"><div className="bg-inner"></div></div>
                      <div className="bg-right"><div className="bg-inner"></div></div>
                      <div className="bg"><div className="bg-inner"></div></div>
                      <span className="text-label"><img src={panierIcon} alt="Panier" style={{ width: '16px', height: '16px', marginRight: '4px' }} />Ajouter au panier</span>
                    </button>
                    <button
                      onClick={() => onAddToWishlist(p)}
                      className={`btn-3d block-cube block-cube-hover ${
                        isInWishlist(p._id) ? 'btn-danger-3d' : 'btn-secondary-3d'
                      }`}
                    >
                      <div className="bg-top"><div className="bg-inner"></div></div>
                      <div className="bg-right"><div className="bg-inner"></div></div>
                      <div className="bg"><div className="bg-inner"></div></div>
                      <span className="text-label">{isInWishlist(p._id) ? '‚ù§Ô∏è Dans ma liste' : 'ü§ç Ajouter √† ma liste'}</span>
                    </button>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
