// Login component: handles user authentication form and calls backend API.
// This is intentionally simple: it sends `{ email, password }` to the backend
// and expects a user object in response. For production, use secure flows.
import React, { useState } from 'react';

export default function Login({ onLogin }) {
  // Local state for the form
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  // Send credentials to backend and invoke `onLogin` with returned user
  async function login() {
    try {
      const res = await fetch('http://localhost:4000/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password })
      });
      if (!res.ok) throw new Error();
      const user = await res.json();
      onLogin(user); // Propagate user to parent App component
    } catch {
      // Show a friendly error message on failure
      setError('Email ou mot de passe incorrect');
    }
  }

  // Render a centered card with email/password inputs and a submit button.
  return (
    <div className="container d-flex justify-content-center align-items-center vh-100">
      <div className="card shadow p-4" style={{ width: 380 }}>
        <h3 className="text-center mb-4">üîê Login</h3>

        <input
          className="form-control mb-3"
          placeholder="Email"
          onChange={e => setEmail(e.target.value)}
        />

        <input
          type="password"
          className="form-control mb-3"
          placeholder="Mot de passe"
          onChange={e => setPassword(e.target.value)}
        />

        <button className="btn btn-primary w-100" onClick={login}>
          Se connecter
        </button>

        {error && <div className="alert alert-danger mt-3">{error}</div>}
      </div>
    </div>
  );
}
